mapper.select:
SELECT id,`name`,`type`,`value`,`order`,`label`,create_time createTime,create_by createBy FROM sys_dictionary
AND `name` = #{name}
ORDER BY create_time DESC

SELECT id,`name`,`type`,`value`,`order`,`label`,create_time createTime,create_by createBy
FROM sys_dictionary
WHERE id = #{id}

SELECT id,`name`,`type`,`value`,`order`,`label`,create_time createTime,create_by createBy
FROM sys_dictionary
WHERE `type` = #{type}
